package basics;

import java.util.Scanner;

public class Tenth {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner s=new Scanner(System.in);
float n=s.nextFloat();
System.out.printf("%.0f\n",n);
System.out.printf("%.1f\n",n);
System.out.printf("%.2f\n",n);
s.nextLine();	
	}

}
